import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

// Configuración global de Axios
axios.defaults.baseURL = 'http://localhost:5000';
axios.interceptors.request.use(config => {
  console.log('Sending request to:', config.url);
  return config;
}, error => {
  console.error('Request error:', error);
  return Promise.reject(error);
});

function App() {
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchBikes = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Fetching bikes...');

      const response = await axios.get('/api/bikes', {
        timeout: 10000 // 10 segundos de timeout
      });

      console.log('Bikes data:', response.data);
      setBikes(response.data);
    } catch (err) {
      console.error('Error fetching bikes:', err);
      setError(err.message || 'Error al cargar las bicicletas');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Primero prueba el endpoint básico
    axios.get('/api/test')
      .then(response => {
        console.log('Backend connection test:', response.data);
        fetchBikes();
      })
      .catch(err => {
        console.error('Backend connection failed:', err);
        setError('No se pudo conectar al servidor. Verifica que el backend esté corriendo.');
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="loading">Cargando...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="App">
      <h1>Tienda de Bicicletas</h1>
      <div className="bike-list">
        <h2>Bicicletas Disponibles</h2>
        <table>
          <thead>
            <tr>
              <th>Modelo</th>
              <th>Tipo</th>
              <th>Precio</th>
              <th>Stock</th>
            </tr>
          </thead>
          <tbody>
            {bikes.map(bike => (
              <tr key={bike.id}>
                <td>{bike.model}</td>
                <td>{bike.bike_type}</td>
                <td>${bike.price}</td>
                <td>{bike.stock}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
