import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BikeCard from './BikeCard';

const BikesComponent = ({ addToCart }) => {
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchBikes = async () => {
    try {
      setLoading(true);
      setError(null);
      await axios.get('/test');
      const response = await axios.get('/bikes');
     setBikes (response.data);
    } catch (err) {
      console.error('Error al cargar datos:', err);
      setError(err.message || 'Error al conectar con el servidor');
      if (process.env.NODE_ENV === 'development') {
        setBikes([{
          id: 1,
        model: "Bicicleta de Montaña",
        bike_type: "montaña",
        age_group: "adulto",
        price: 1200000,
        discount: 10,
        rating: 4,
        description: "Bicicleta todo terreno",
        image_url: "ruta/a/imagen.jpg",
        images: ["ruta/a/imagen1.jpg", "ruta/a/imagen2.jpg"],
        stock: 5,
        is_available: true
        }]);
        setError('⚠ Usando datos de ejemplo (problema de conexión)');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchBikes(); }, []);



  if (loading) return <div className="loading"><div className="spinner"></div><p>Cargando inventario...</p></div>;
  if (error) return <div className="error"><h2>Error</h2><p>{error}</p><button onClick={fetchBikes}>Reintentar conexión</button></div>;

  return (
    <div className="bike-shop-container">
      <h1>Tienda de Bicicletas Premium</h1>
      
      <div className="bike-grid">
        {bikes.map(bike => (
          <BikeCard 
            key={bike.id}
            bike={{
              ...bike,
              name: bike.model,
              type: bike.bike_type,
              images: [bike.image_url || 'default-bike.jpg'] // Imagen por defecto
            }}
            addToCart={addToCart}
          />
        ))}
      </div>
    </div>
  );
};

export default BikesComponent;
