import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { formatPhoneNumber } from '../utils/helpers';
import './Suppliers.css'; // Asegúrate que la ruta sea correcta


const Suppliers = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchSuppliers = async () => {
    try {
      console.log("Iniciando petición a /api/suppliers");
      const response = await axios.get('http://localhost:5001/api/suppliers');
      console.log("Respuesta recibida:", response);
      
      if (!response.data) {
        throw new Error("No se recibieron datos");
      }
      
      setSuppliers(response.data);
      setError(null);
    } catch (err) {
      console.error("Error completo:", err);
      setError(`Error al cargar proveedores: ${err.message}`);
      
      // Datos de ejemplo para desarrollo
      if (process.env.NODE_ENV === 'development') {
        setSuppliers([
          {
            id: 1,
            name: "Proveedor Ejemplo",
            contact_email: "proveedor@ejemplo.com",
            phone: "3001234567",
            component_specialty: "Cuadros"
          }
        ]);
        setError("⚠ Modo desarrollo: Usando datos de ejemplo");
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSuppliers();
  }, []);

  if (loading) return <div className="loading">Cargando proveedores...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="suppliers-container">
      <h2>Gestión de Proveedores</h2>
      
      <div className="supplier-form">
        <h3>{editingId ? 'Editar Proveedor' : 'Agregar Nuevo Proveedor'}</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Nombre:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="contact_email"
              value={formData.contact_email}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Teléfono:</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Especialidad:</label>
            <select
              name="component_specialty"
              value={formData.component_specialty}
              onChange={handleInputChange}
              required
            >
              <option value="">Seleccione...</option>
              <option value="Cuadros">Cuadros</option>
              <option value="Ruedas">Ruedas</option>
              <option value="Frenos">Frenos</option>
              <option value="Transmisión">Transmisión</option>
              <option value="Sillines">Sillines</option>
              <option value="Manillares">Manillares</option>
              <option value="Pedales">Pedales</option>
            </select>
          </div>
          
          <div className="form-actions">
            <button type="submit">
              {editingId ? 'Actualizar' : 'Agregar'}
            </button>
            {editingId && (
              <button type="button" onClick={resetForm}>
                Cancelar
              </button>
            )}
          </div>
        </form>
      </div>
      
      <div className="suppliers-list">
        <h3>Proveedores Registrados</h3>
        {suppliers.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Contacto</th>
              <th>Teléfono</th>
              <th>Especialidad</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {suppliers.map(supplier => (
              <tr key={supplier.id}>
                <td>{supplier.name}</td>
                <td>{supplier.contact_email}</td>
                <td>{formatPhoneNumber(supplier.phone)}</td>
                <td>{supplier.component_specialty}</td>
                <td>
                  <button onClick={() => handleEdit(supplier)}>Editar</button>
                  <button onClick={() => handleDelete(supplier.id)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        ) : (
          <p>No hay proveedores registrados</p>
        )}
      </div>
    </div>
  );
};

export default Suppliers;
