import React, { useState } from 'react';
import { FaShoppingCart, FaStar, FaRegStar } from 'react-icons/fa';

function BikeCard({ bike, addToCart }) {
  const [addedToCart, setAddedToCart] = useState(false);

  // Valores por defecto
  const bikeData = {
    name: bike.name || "Bicicleta",
    type: bike.type || "general",
    age_group: bike.age_group || "adulto",
    rating: bike.rating || 0,
    description: bike.description || "Descripción no disponible",
    price: bike.price || 0,
    discount: bike.discount || 0,
    images: bike.images || ["default-bike.jpg"]
  };

  const handleAddToCart = () => {
    // Verifica que addToCart existe antes de usarla
    if (typeof addToCart === 'function') {
      addToCart({
        id: bike.id || Date.now(), // Asegura un ID único
        name: bikeData.name,
        type: bikeData.type,
        price: bikeData.price,
        discount: bikeData.discount,
        quantity: quantity, // Usa la cantidad actual del estado
        image: bikeData.images[0] // Primera imagen como miniatura
      });
      setAddedToCart(true);
      setTimeout(() => setAddedToCart(false), 2000); // Mensaje desaparece después de 2 segundos
    }
  };

  return (
    <div className="bike-card">
      <div className="bike-image">
        <img src={mainImage} alt={bike.name} />
        <div className="thumbnail-images">
          {bike.images.map((img, index) => (
            <img 
              key={index} 
              src={img} 
              alt={`Vista ${index + 1}`} 
              onClick={() => setMainImage(img)}
              className={mainImage === img ? 'active' : ''}
            />
          ))}
        </div>
      </div>
      <div className="bike-info">
        <h3>{bike.name}</h3>
        <div className="bike-meta">
          <span className={`type-badge ${bike.type}`}>{bike.type}</span>
          <span className={`age-badge ${bike.age_group}`}>{bike.age_group}</span>
        </div>
        <div className="rating">
          {[...Array(5)].map((_, i) => (
            i < bike.rating ? <FaStar key={i} /> : <FaRegStar key={i} />
          ))}
        </div>
        <p className="description">{bike.description}</p>
        <div className="price-section">
          <span className="price">${bike.price}</span>
          {bike.discount && (
            <span className="discount">-{bike.discount}%</span>
          )}
        </div>
        <div className="cart-controls">
          <div className="quantity-selector">
            <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>-</button>
            <span>{quantity}</span>
            <button onClick={() => setQuantity(quantity + 1)}>+</button>
          </div>
          <button className="add-to-cart" onClick={handleAddToCart}>
            <FaShoppingCart /> Añadir
          </button>
        </div>
      </div>
    </div>
  );
}

export default BikeCard;
