import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';


function App() {
  const [bikes, setBikes] = useState([]);
  const [view, setView] = useState('bikes');

  useEffect(() => {
    fetchBikes();
  }, []);

  const fetchBikes = async () => {
    const response = await axios.get('http://localhost:5000/api/bikes');
    //const data = await response.json();
    setBikes(data);
  };

  const handleSell = async (bikeId) => {
    const quantity = prompt('Ingrese cantidad a vender:');
    if (quantity) {
      const response = await fetch('http://localhost:5000/api/bikes/sell', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ bike_id: bikeId, quantity: parseInt(quantity) })
      });
      const result = await response.json();
      alert(result.msg);
      fetchBikes();
    }
  };

  const handleRent = async (bikeId) => {
    const days = prompt('Ingrese días de arriendo:');
    if (days) {
      const response = await fetch('http://localhost:5000/api/bikes/rent', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ bike_id: bikeId, rental_days: parseInt(days) })
      });
      const result = await response.json();
      alert(`${result.msg}. Total: $${result.rental.total_amount}`);
      fetchBikes();
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Tienda de Bicicletas</h1>
        <nav>
          <button onClick={() => setView('bikes')}>Bicicletas</button>
          <button onClick={() => setView('inventory')}>Inventario</button>
          <button onClick={() => setView('suppliers')}>Proveedores</button>
        </nav>
      </header>

      {view === 'bikes' && (
        <div className="bike-list">
          <h2>Bicicletas Disponibles</h2>
          <table>
            <thead>
              <tr>
                <th>Modelo</th>
                <th>Tipo</th>
                <th>Precio</th>
                <th>Arriendo/día</th>
                <th>Stock</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {bikes.map(bike => (
                <tr key={bike.id}>
                  <td>{bike.model}</td>
                  <td>{bike.bike_type}</td>
                  <td>${bike.price}</td>
                  <td>${bike.rental_price}</td>
                  <td>{bike.stock}</td>
                  <td>
                    <button onClick={() => handleSell(bike.id)}>Vender</button>
                    <button 
                      onClick={() => handleRent(bike.id)}
                      disabled={!bike.is_available}
                    >
                      {bike.is_available ? 'Arrendar' : 'No disponible'}
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Otras vistas (inventory, suppliers) serían similares */}
    </div>
  );
}

export default App;
