import React, { useState, useEffect } from 'react';
import axios from 'axios';

import './Suppliers.css'; // Asegúrate que la ruta sea correcta
import { formatPhoneNumber } from 'utils/helpers';

const Suppliers = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    contact_email: '',
    phone: '',
    component_specialty: ''
  });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchSuppliers();
  }, []);



  const fetchSuppliers = async () => {
  try {
    setLoading(true);
    console.log("Iniciando fetch de proveedores..."); // Debug 1
    const response = await axios.get('/suppliers'); // URL completa
    setSuppliers(response.data);
    console.log("Datos recibidos:", response.data); // Debug 2
    setSuppliers(response.data);
  } catch (err) {
    console.error("Error completo:", err); // Debug detallado
    setError('Error al cargar proveedores');
  } finally {
    setLoading(false);
  }
};



  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`/api/suppliers/${editingId}`, formData);
      } else {
        await axios.post('/api/suppliers', formData);
      }
      fetchSuppliers();
      resetForm();
    } catch (err) {
      setError(editingId ? 'Error al actualizar' : 'Error al agregar');
      console.error(err);
    }
  };

  const handleEdit = (supplier) => {
    setFormData({
      name: supplier.name,
      contact_email: supplier.contact_email,
      phone: supplier.phone,
      component_specialty: supplier.component_specialty
    });
    setEditingId(supplier.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Estás seguro de eliminar este proveedor?')) {
      try {
        await axios.delete(`/api/suppliers/${id}`);
        fetchSuppliers();
      } catch (err) {
        setError('Error al eliminar proveedor');
        console.error(err);
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      contact_email: '',
      phone: '',
      component_specialty: ''
    });
    setEditingId(null);
  };

  if (loading) return <div className="loading">Cargando proveedores...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="suppliers-container">
      <h2>Gestión de Proveedores</h2>
      
      <div className="supplier-form">
        <h3>{editingId ? 'Editar Proveedor' : 'Agregar Nuevo Proveedor'}</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Nombre:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="contact_email"
              value={formData.contact_email}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Teléfono:</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Especialidad:</label>
            <select
              name="component_specialty"
              value={formData.component_specialty}
              onChange={handleInputChange}
              required
            >
              <option value="">Seleccione...</option>
              <option value="Cuadros">Cuadros</option>
              <option value="Ruedas">Ruedas</option>
              <option value="Frenos">Frenos</option>
              <option value="Transmisión">Transmisión</option>
              <option value="Sillines">Sillines</option>
              <option value="Manillares">Manillares</option>
              <option value="Pedales">Pedales</option>
            </select>
          </div>
          
          <div className="form-actions">
            <button type="submit">
              {editingId ? 'Actualizar' : 'Agregar'}
            </button>
            {editingId && (
              <button type="button" onClick={resetForm}>
                Cancelar
              </button>
            )}
          </div>
        </form>
      </div>
      
      <div className="suppliers-list">
        <h3>Proveedores Registrados</h3>
        {suppliers.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Contacto</th>
              <th>Teléfono</th>
              <th>Especialidad</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {suppliers.map(supplier => (
              <tr key={supplier.id}>
                <td>{supplier.name}</td>
                <td>{supplier.contact_email}</td>
                <td>{formatPhoneNumber(supplier.phone)}</td>
                <td>{supplier.component_specialty}</td>
                <td>
                  <button onClick={() => handleEdit(supplier)}>Editar</button>
                  <button onClick={() => handleDelete(supplier.id)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        ) : (
          <p>No hay proveedores registrados</p>
        )}
      </div>
    </div>
  );
};

export default Suppliers;
