import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BikeCard from './BikeCard';

const BikesComponent = ({ addToCart }) => {
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchBikes = async () => {
    try {
      setLoading(true);
      setError(null);
      // Cambia esta URL por tu endpoint correcto
      const response = await axios.get('http://localhost:5001/api/bikes');
      setBikes(response.data);
    } catch (err) {
      console.error('Error fetching bikes:', err);
      setError('Error al conectar con el servidor');
      // Datos de ejemplo mejor estructurados
      setBikes([{
        id: 1,
        model: "Bicicleta de Montaña",
        bike_type: "montaña",
        age_group: "adulto",
        price: 1200000,
        discount: 10,
        rating: 4,
        description: "Bicicleta todo terreno",
        image_url: "ruta/a/imagen.jpg",
        images: ["ruta/a/imagen1.jpg", "ruta/a/imagen2.jpg"],
        stock: 5,
        is_available: true
      }]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchBikes(); }, []);

  if (loading) return <div className="loading">Cargando...</div>;

  return (
    <div className="bikes-container">
      {error && (
        <div className="error-banner">
          <p>{error}</p>
          <button onClick={fetchBikes}>Reintentar conexión</button>
        </div>
      )}
      
      <div className="bike-grid">
        {bikes.map(bike => (
          <BikeCard 
            key={bike.id}
            bike={bike}
            addToCart={addToCart}
          />
        ))}
      </div>
    </div>
  );
};

export default BikesComponent;
