import React from 'react';
import BikesComponent from './BikesComponent';


const BikeShop = ({ addToCart }) => {
  return (
    <div className="bike-shop">
      <BikesComponent addToCart={addToCart} />
      
      {/* Versión alternativa usando BikeCard */}
      <div className="bike-grid">
        {/* Esto sería dinámico cuando conectes con los datos */}
        <BikeCard 
          bike={{
            name: "Bicicleta de Montaña",
            type: "montaña",
            age_group: "adulto",
            rating: 4,
            description: "Bicicleta todo terreno",
            price: 1200000,
            discount: 10,
            images: ["url-imagen-1.jpg", "url-imagen-2.jpg"]
          }}
          addToCart={addToCart}
        />
      </div>
    </div>
  );
};

export default BikeShop;
